  $id: String!
) {
  markdownRemark(id: {eq: $id}) {
    html
    frontmatter {
      path
      title
      templateKey
      cover_image {
        childImageSharp {
          fluid(maxHeight: 1200, duotone: {highlight: "#4644FD", shadow: "#222172", opacity: 65}) {
            ...GatsbyImageSharpFluid
          }
        }
      }
    }
  }
}

